    private void jMenu3_AltaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jMenu3_AltaMouseClicked
        jLabel1_MensajeError.setText("");
        JDialogPedirDatos pedirDatos = new JDialogPedirDatos(this, true);
        pedirDatos.setVisible(true);
    }//GEN-LAST:event_jMenu3_AltaMouseClicked

    private void jMenu4_BajaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jMenu4_BajaMouseClicked
        jLabel1_MensajeError.setText("");
        int fila;
        fila = jTable1_TablaContactos.getSelectedRow();

        int[] variasFilas;
        variasFilas = new int[jTable1_TablaContactos.getSelectedRows().length];

        variasFilas = jTable1_TablaContactos.getSelectedRows();
        if (fila == -1) {
            jLabel1_MensajeError.setText("Selecciona una fila.");
        } else {
            int cont = 0;
            for (int variasFila : variasFilas) {
                DefaultTableModel dtm = (DefaultTableModel) jTable1_TablaContactos.getModel();
                dtm.removeRow(variasFila - cont);
                cont++;
            }
        }
    }//GEN-LAST:event_jMenu4_BajaMouseClicked

