    public void addCliente(Cliente cliente) {
        DefaultTableModel dtm = (DefaultTableModel) jTable1_TablaContactos.getModel();
        dtm.addRow(cliente.clienteArray());
    }

    private void inicializarTabla() {
        DefaultTableModel dtm = new DefaultTableModel();
        dtm.setColumnIdentifiers(new String[]{"Nombre", "Apellido1", "Apellido2", "DNI"});
        jTable1_TablaContactos.setModel(dtm);
    }
